import React from "react";
import ThemeToggle from "./ThemeToggle";

const FONT_OPTIONS = [
  { label: "Inter (Default)", value: "'Inter', system-ui, sans-serif" },
  { label: "Roboto", value: "'Roboto', Arial, sans-serif" },
  { label: "Lato", value: "'Lato', Arial, sans-serif" },
  { label: "Merriweather", value: "'Merriweather', serif" },
  { label: "Fira Mono", value: "'Fira Mono', monospace" },
  { label: "Comic Neue", value: "'Comic Neue', cursive, sans-serif" },
];

function Header({ children }) {
  // Font picker logic
  const [font, setFont] = React.useState(() => {
    return localStorage.getItem("keeper-font") || "'Inter', system-ui, sans-serif";
  });
  React.useEffect(() => {
    document.documentElement.style.setProperty('--font-family', font);
    localStorage.setItem("keeper-font", font);
  }, [font]);

  return (
    <header className="keeper-header">
      <h1>
        <span role="img" aria-label="notebook">üìù</span> Keeper
      </h1>
      <select
        className="font-picker"
        value={font}
        onChange={e => setFont(e.target.value)}
        aria-label="Choose app font"
      >
        {FONT_OPTIONS.map(opt => (
          <option key={opt.value} value={opt.value} style={{ fontFamily: opt.value }}>{opt.label}</option>
        ))}
      </select>
      <ThemeToggle />
      {children}
    </header>
  );
}

export default Header;
