import React, { useState, useEffect } from "react";
import { ThemeProvider } from "../ThemeContext";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import TagSelector from "./TagSelector";
import "../keeper.css";

const defaultTags = ["Personal", "Work", "Idea", "Urgent", "Todo"];

function App() {
  const [notes, setNotes] = useState([]);
  const [search, setSearch] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [tags, setTags] = useState(defaultTags);
  const [filterTags, setFilterTags] = useState([]);

  useEffect(() => {
    const savedNotes = localStorage.getItem("keeper-notes");
    if (savedNotes) setNotes(JSON.parse(savedNotes));
    const savedTags = localStorage.getItem("keeper-tags");
    if (savedTags) setTags(JSON.parse(savedTags));
  }, []);

  useEffect(() => {
    localStorage.setItem("keeper-notes", JSON.stringify(notes));
  }, [notes]);

  useEffect(() => {
    localStorage.setItem("keeper-tags", JSON.stringify(tags));
  }, [tags]);

  function addNote(newNote) {
    if (!newNote.title.trim() && !newNote.content.trim()) return;
    setNotes(prevNotes => [{ ...newNote, tags: newNote.tags || [] }, ...prevNotes]);
    // Add new tags to global tag list
    if (newNote.tags) {
      setTags(prevTags => Array.from(new Set([...prevTags, ...newNote.tags])));
    }
  }

  function deleteNote(id) {
    setNotes(prevNotes => prevNotes.filter((_, idx) => idx !== id));
  }

  function startEdit(id) {
    setEditingId(id);
  }

  function editNote(id, updatedNote) {
    setNotes(prevNotes => prevNotes.map((note, idx) => idx === id ? { ...updatedNote, tags: updatedNote.tags || [] } : note));
    setEditingId(null);
    // Add new tags to global tag list
    if (updatedNote.tags) {
      setTags(prevTags => Array.from(new Set([...prevTags, ...updatedNote.tags])));
    }
  }

  // Filter notes by search and tags
  const filteredNotes = notes.filter(note => {
    const matchesSearch =
      note.title.toLowerCase().includes(search.toLowerCase()) ||
      note.content.toLowerCase().includes(search.toLowerCase());
    const matchesTags =
      filterTags.length === 0 || (note.tags && filterTags.every(tag => note.tags.includes(tag)));
    return matchesSearch && matchesTags;
  });

  return (
    <ThemeProvider>
      <div className="keeper-app">
        <Header />
        <CreateArea onAdd={addNote} search={search} setSearch={setSearch} tags={tags} />
        <TagSelector tags={tags} selectedTags={filterTags} onChange={setFilterTags} allowCreate={false} />
        <div className="notes-grid">
          {filteredNotes.length === 0 ? (
            <p className="empty-msg">No notes found. Add one!</p>
          ) : (
            filteredNotes.map((noteItem, index) => (
              <Note
                key={index}
                id={index}
                title={noteItem.title}
                content={noteItem.content}
                tags={noteItem.tags || []}
                onDelete={deleteNote}
                onEdit={startEdit}
                editing={editingId === index}
                onSaveEdit={editNote}
                allTags={tags}
              />
            ))
          )}
        </div>
        <Footer />
      </div>
    </ThemeProvider>
  );
}

export default App;
