import React, { useState, useRef, useEffect } from "react";

function Note({ id, title, content, onDelete, onEdit, editing, onSaveEdit }) {
  const [editMode, setEditMode] = useState(editing);
  const [editNote, setEditNote] = useState({ title, content });
  const noteRef = useRef(null);

  useEffect(() => {
    setEditMode(editing);
    setEditNote({ title, content });
    if (editing && noteRef.current) noteRef.current.scrollIntoView({ behavior: "smooth", block: "center" });
  }, [editing, title, content]);

  function handleDelete() {
    onDelete(id);
  }

  function handleEdit() {
    setEditMode(true);
    onEdit(id);
  }

  function handleChange(e) {
    const { name, value } = e.target;
    setEditNote(prev => ({ ...prev, [name]: value }));
  }

  function handleSave() {
    if (!editNote.title.trim() && !editNote.content.trim()) return;
    onSaveEdit(id, editNote);
    setEditMode(false);
  }

  function handleCancel() {
    setEditMode(false);
    setEditNote({ title, content });
    onSaveEdit(id, { title, content });
  }

  return (
    <div className={`note fade-in`} ref={noteRef}>
      {editMode ? (
        <>
          <input
            name="title"
            value={editNote.title}
            onChange={handleChange}
            className="note-edit-title"
            aria-label="Edit note title"
          />
          <textarea
            name="content"
            value={editNote.content}
            onChange={handleChange}
            className="note-edit-content"
            rows="3"
            aria-label="Edit note content"
          />
          <div className="note-edit-actions">
            <button className="save-btn" onClick={handleSave} aria-label="Save note">Save</button>
            <button className="cancel-btn" onClick={handleCancel} aria-label="Cancel edit">Cancel</button>
          </div>
        </>
      ) : (
        <>
          <h1>{title}</h1>
          <p>{content}</p>
          <div className="note-actions">
            <button className="edit-btn" onClick={handleEdit} aria-label="Edit note">‚úé</button>
            <button className="delete-btn" onClick={handleDelete} aria-label="Delete note">üóëÔ∏è</button>
          </div>
        </>
      )}
    </div>
  );
}

export default Note;
