import React, { useState, useEffect } from "react";
import { ThemeProvider } from "../ThemeContext";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import TagSelector from "./TagSelector";
import "../keeper.css";

const defaultTags = ["Personal", "Work", "Idea", "Urgent", "Todo"];

function App() {
  const [notes, setNotes] = useState([]);
  const [search, setSearch] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [tags, setTags] = useState(defaultTags);
  const [filterTags, setFilterTags] = useState([]);
  const [pinnedIds, setPinnedIds] = useState([]);

  useEffect(() => {
    const savedNotes = localStorage.getItem("keeper-notes");
    if (savedNotes) setNotes(JSON.parse(savedNotes));
    const savedTags = localStorage.getItem("keeper-tags");
    if (savedTags) setTags(JSON.parse(savedTags));
    const savedPinned = localStorage.getItem("keeper-pinned");
    if (savedPinned) setPinnedIds(JSON.parse(savedPinned));
  }, []);

  useEffect(() => {
    localStorage.setItem("keeper-notes", JSON.stringify(notes));
  }, [notes]);
  useEffect(() => {
    localStorage.setItem("keeper-tags", JSON.stringify(tags));
  }, [tags]);
  useEffect(() => {
    localStorage.setItem("keeper-pinned", JSON.stringify(pinnedIds));
  }, [pinnedIds]);

  function addNote(newNote) {
    if (!newNote.title.trim() && !newNote.content.trim()) return;
    setNotes(prevNotes => [{ ...newNote, tags: newNote.tags || [], checklist: newNote.checklist || [] }, ...prevNotes]);
    if (newNote.tags) {
      setTags(prevTags => Array.from(new Set([...prevTags, ...newNote.tags])));
    }
  }

  function deleteNote(id) {
    setNotes(prevNotes => prevNotes.filter((_, idx) => idx !== id));
    setPinnedIds(prev => prev.filter(idx => idx !== id));
  }

  function startEdit(id) {
    setEditingId(id);
  }

  function editNote(id, updatedNote) {
    setNotes(prevNotes => prevNotes.map((note, idx) => idx === id ? { ...updatedNote, tags: updatedNote.tags || [], checklist: updatedNote.checklist || [] } : note));
    setEditingId(null);
    if (updatedNote.tags) {
      setTags(prevTags => Array.from(new Set([...prevTags, ...updatedNote.tags])));
    }
  }

  function togglePin(id) {
    setPinnedIds(prev => prev.includes(id) ? prev.filter(i => i !== id) : [id, ...prev]);
  }

  // Filter notes by search and tags
  const filteredNotes = notes.filter(note => {
    const matchesSearch =
      note.title.toLowerCase().includes(search.toLowerCase()) ||
      note.content.toLowerCase().includes(search.toLowerCase());
    const matchesTags =
      filterTags.length === 0 || (note.tags && filterTags.every(tag => note.tags.includes(tag)));
    return matchesSearch && matchesTags;
  });

  // Split pinned and unpinned notes
  const pinnedNotes = filteredNotes.filter((_, idx) => pinnedIds.includes(idx));
  const unpinnedNotes = filteredNotes.filter((_, idx) => !pinnedIds.includes(idx));

  return (
    <ThemeProvider>
      <div className="keeper-app">
        <Header />
        <CreateArea onAdd={addNote} search={search} setSearch={setSearch} tags={tags} />
        <TagSelector tags={tags} selectedTags={filterTags} onChange={setFilterTags} allowCreate={false} />
        <div className="notes-grid">
          {pinnedNotes.length > 0 && <div className="pinned-label">📌 Pinned</div>}
          {pinnedNotes.map((noteItem, index) => (
            <Note
              key={"pinned-" + index}
              id={notes.indexOf(noteItem)}
              title={noteItem.title}
              content={noteItem.content}
              tags={noteItem.tags || []}
              checklist={noteItem.checklist || []}
              onDelete={deleteNote}
              onEdit={startEdit}
              editing={editingId === notes.indexOf(noteItem)}
              onSaveEdit={editNote}
              onTogglePin={togglePin}
              pinned={true}
              allTags={tags}
            />
          ))}
          {pinnedNotes.length > 0 && <hr className="pinned-separator" />}
          {unpinnedNotes.length === 0 && pinnedNotes.length === 0 ? (
            <p className="empty-msg">No notes found. Add one!</p>
          ) : (
            unpinnedNotes.map((noteItem, index) => (
              <Note
                key={"unpinned-" + index}
                id={notes.indexOf(noteItem)}
                title={noteItem.title}
                content={noteItem.content}
                tags={noteItem.tags || []}
                checklist={noteItem.checklist || []}
                onDelete={deleteNote}
                onEdit={startEdit}
                editing={editingId === notes.indexOf(noteItem)}
                onSaveEdit={editNote}
                onTogglePin={togglePin}
                pinned={false}
                allTags={tags}
              />
            ))
          )}
        </div>
        <Footer />
      </div>
    </ThemeProvider>
  );
}

export default App;
